import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Alert, Collapse, Container, Button } from 'reactstrap';
import './Wardrobe.css'
import Wardrobe_list from './Wardrobe_list';
import { Row, Col } from 'reactstrap';
import Sidebar from './Sidebar'
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

export default class Wardrobe extends Component {

    static propTypes = {

    }

    constructor(props) {
        super(props);
        this.state = {
            lists: [['Hoodie', 'hoodie', 'none', 'none'],
            ['Shirt', 'none', 'none', 'none'],
            ['Jacket', 'none', 'none', 'none'],
            ['Sweatshirts', 'none', 'none', 'none'],
            ['T-shirts', 'none', 'none', 'none'],
            ['Dress', 'none', 'none', 'none'],
            ['Shorts', 'none', 'none', 'none'],
            ['Trousers', 'none', 'none', 'none'],
            ['Shoes', 'none', 'none', 'none']],
            buttonToggle: false
        }
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {

    }

    componentWillUnmount() {

    }

    componentWillReceiveProps(nextProps) {

    }
    render() {
        let SidebarClass = this.state.buttonToggle ? 'sidebar open' : 'sidebar';
        let ContentClass = this.state.buttonToggle ? 'content open' : 'content';
        return (
            <div>
                {/* <h1 className="text-warning">WARDROBE</h1> */}
                <div className='w-lists'> 
                    <SideNav className = {SidebarClass} onToggle={this.handleClick}
                    >
                        <SideNav.Toggle />
                        
                    </SideNav>
                    {console.log(ContentClass)}
                    <Wardrobe_list className={ContentClass} lists={this.state.lists} expanded={this.state.buttonToggle} />
                </div>
            </div>
        )
    }

    handleClick() {
        this.setState((prevState, props) => ({
            buttonToggle: !prevState.buttonToggle
        }));
    }
}
