export function filterReducer(state = '', action) {
    switch (action.type) {
        case '@FILTERS/SET_FILTERS':
            return action.filters;
        default:
            return state;
    }
}

const initPostState = {
    item: [
        {
            id: 1,
            tags: ['women','hoodie']
        },
        {
            id: 1,
            tags:['men','shirt']
        },
        {
            id: 2,
            tags:['hoodie']
        }

    ]
}

export function wardrobeReducer(state = initPostState, action) {
    switch (action.type) {
        case '@POST/END_LIST_POSTS':
            return {
                ...state,
                posts: action.posts,
                hasMore: action.posts.length > 0
            };
        case '@POST/END_CREATE_POST':
            var newPosts = [...state.posts];
            newPosts = [action.post, ...newPosts];
            return {
                ...state,
                posts: newPosts,
            };
        default:
            return state;
    }
}
