import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Alert, Collapse, Container, Button, Input } from 'reactstrap';
import './Wardrobe.css'
import Wardrobe_list from './Wardrobe_list';
import { Row, Col } from 'reactstrap';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
// import '@trendmicro/react-sidenav/dist/react-sidenav.css';
import './react-sidenav.css'

export default class Wardrobe extends Component {

    static propTypes = {

    }

    constructor(props) {
        super(props);
        this.state = {
            lists: [['Hoodie', 'hoodie', 'none', 'none'],
            ['Shirt', 'none', 'none', 'none'],
            ['Jacket', 'none', 'none', 'none'],
            ['Sweatshirts', 'none', 'none', 'none'],
            ['T-shirts', 'none', 'none', 'none'],
            ['Dress', 'none', 'none', 'none'],
            ['Shorts', 'none', 'none', 'none'],
            ['Trousers', 'none', 'none', 'none'],
            ['Shoes', 'none', 'none', 'none']],
            buttonToggle: false,
            searchText: ''
        }

        this.searchEl = null;
        this.handleSearchKeyPress = this.handleSearchKeyPress.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {

    }

    componentWillUnmount() {

    }

    componentWillReceiveProps(nextProps) {

    }
    render() {
        let sidebarClass = this.state.buttonToggle ? 'sidebar-open' : 'sidebar';
        return (
            <div>
                {/* <h1 className="text-warning">WARDROBE</h1> */}
                <div className='w-lists'>
                    <SideNav onToggle={this.handleClick}>
                        <Collapse isOpen={this.state.buttonToggle} SideNav>
                            <div className='search ml-auto'>
                                <Input className='ml-auto' type='text' innerRef={this.searchEl} placeholder='Search' onKeyPress={this.handleSearchKeyPress} innerRef={e => this.searchEl = e}></Input>{
                                    this.state.searchText &&
                                    <i className='navbar-text fa fa-times' onClick={this.handleClearSearch}></i>
                                }
                            </div>
                        </Collapse>

                        <SideNav.Toggle />

                    </SideNav>
                    <Wardrobe_list lists={this.state.lists} expanded={this.state.buttonToggle} />
                </div>
            </div>
        )
    }

    handleClick() {
        this.setState((prevState, props) => ({
            buttonToggle: !prevState.buttonToggle
        }));
    }

    handleSearchKeyPress(e) {
        // var keyCode = e.keyCode || e.which;
        // if (keyCode === 13){
        //     this.setState({
        //         searchText: e.target.value
        //     });
        // }
    }
}
