import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './WardrobeItem.css'

class TagItem extends Component {
    static propTypes = {
        id: PropTypes.number,
        tags: PropTypes.array,
    }

    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    render() {
        const {id, photo, tags , expanded} = this.props;
        let contentClass = expanded ? 'open' : '';
        return (
            <div>
                <div className={`item ${contentClass}`}>
                    <span className='rectangle'><img className='img' src={photo} alt={id} onClick={this.handleClick}/></span>
                </div>
            </div>
        )
    }
    handleClick() {
        this.setState((prevState, props) => ({
            buttonToggle: !prevState.buttonToggle
        }));
    }
}

export default connect(state => ({
    filter: state.filter
}))(TagItem);
