import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './WardrobeItem.css'
import { connect } from 'react-redux';
import { createTag } from '../actions/post-actions';

class TagItem extends Component {
    static propTypes = {
        id: PropTypes.string,
        tags: PropTypes.array,
    }

    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    render() {
        const {id, photo, tags , expanded} = this.props;
        let contentClass = expanded ? 'open' : '';

        return (
            <div>
                <div className={`item ${contentClass}`}>
                    <span className='rectangle'><img className='img' src={photo} alt={id} onClick={this.handleClick}/></span>
                </div>
            </div>
        )
    }
    handleClick() {
        const {id, photo, tags} = this.props;
        console.log('tag' + tags)
        var newTag = [
            {
                id: id,
                photo: photo,
                tags: tags
            }
        ]
        console.log('newTag: ' + newTag[0].tags)
        this.props.dispatch(createTag(newTag))
    }
}

export default connect(state => ({
    filter: state.filter
}))(TagItem);
