import React from 'react';
import PropTypes from 'prop-types';
import {
    ListGroup,
    ListGroupItem
} from 'reactstrap';

import PostItem from './PostItem.jsx';
// import {createVote} from 'api/posts.js';

import './PostList.css';
import Logo from '../pictures/Floral-Shirt-and-Ripped-Denim-Jeans-with-mosakinns.jpg'

export default class PostList extends React.Component {
    static propTypes = {
        id: PropTypes.number,
        posts: PropTypes.array,
        filter: PropTypes.string,
        onVote: PropTypes.func,
        JacketTag: PropTypes.string,
        TopTag: PropTypes.string,
        BottomTag: PropTypes.string,
        ShoesTag: PropTypes.string
    };

    constructor(props) {
        super(props);

        this.state = {
        };

        this.handleVote = this.handleVote.bind(this);
    }

    render() {
        const {posts} = this.props;

        let children = (
            <ListGroupItem className='empty justify-content-center align-items-center'>
                <img src={Logo} alt="picture" width="150" height="150"/>
            </ListGroupItem >
        );
        if (posts.length) {
            children = posts.map(p => (
                <ListGroupItem key={p.id} action>
                    <PostItem {...p} onVote={this.handleVote} />
                </ListGroupItem>
            ));
        }

        return (
            <div className='post-list d-flex mb-3'>
                <ListGroup className="p-1 flex-fill bg-info"> {children} </ListGroup>
                <ListGroup className="p-5 flex-fill bg-warning"> {children} </ListGroup>
                <ListGroup className="p-5 flex-fill bg-primary"> {children} </ListGroup>
                <ListGroup className="p-5 flex-fill bg-warning"> {children} </ListGroup>
            </div>
        );
    }

    handleVote(id, mood) {
        this.props.onVote(id, mood);
    }
}
