import React from 'react';
import PropTypes from 'prop-types';
import {
    ListGroup,
    ListGroupItem
} from 'reactstrap';

import PostItem from './PostItem.jsx';
// import {createVote} from 'api/posts.js';

import './PostList.css';
import Logo from '../pictures/Floral-Shirt-and-Ripped-Denim-Jeans-with-mosakinns.jpg'
import Gallery from 'react-grid-gallery';

export default class PostList extends React.Component {
    static propTypes = {
        id: PropTypes.number,
        posts: PropTypes.array,
        filter: PropTypes.string,
        onVote: PropTypes.func,
        JacketTag: PropTypes.string,
        TopTag: PropTypes.string,
        BottomTag: PropTypes.string,
        ShoesTag: PropTypes.string
    };

    constructor(props) {
        super(props);

        this.state = {
        };

        this.handleVote = this.handleVote.bind(this);
    }

    render() {
        const {posts} = this.props;

        const IMAGES =
        [{
                src: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_b.jpg",
                thumbnail: "https://c2.staticflickr.com/9/8817/28973449265_07e3aa5d2e_n.jpg",
                thumbnailWidth: 320,
                thumbnailHeight: 174,
                isSelected: true,
                caption: "After Rain (Jeshu John - designerspics.com)"
        },
        {
                src: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_b.jpg",
                thumbnail: "https://c2.staticflickr.com/9/8356/28897120681_3b2c0f43e0_n.jpg",
                thumbnailWidth: 320,
                thumbnailHeight: 212,
                tags: [{value: "Ocean", title: "Ocean"}, {value: "People", title: "People"}],
                caption: "Boats (Jeshu John - designerspics.com)"
        },
        
        {
                src: "https://c4.staticflickr.com/9/8887/28897124891_98c4fdd82b_b.jpg",
                thumbnail: "https://c4.staticflickr.com/9/8887/28897124891_98c4fdd82b_n.jpg",
                thumbnailWidth: 320,
                thumbnailHeight: 212
        }]

        let children = (
            <ListGroupItem className='empty align-content-stretch'>
                <img src={Logo} alt="picture" width="300" height="600"/>
            </ListGroupItem >
        );
        if (posts.length) {
            children = posts.map(p => (
                <ListGroupItem key={p.id} action>
                    <PostItem {...p} onVote={this.handleVote} />
                </ListGroupItem>
            ));
        }

        return (
            // <div className='post-list d-flex '>
            //     <ListGroup className="p-4 flex-fill bg-info"> {children} </ListGroup>
            //     <ListGroup className="p-4 flex-fill bg-warning"> {children} </ListGroup>
            //     <ListGroup className="p-4 flex-fill bg-primary"> {children} </ListGroup>
            //     <ListGroup className="p-4 flex-fill bg-warning"> {children} </ListGroup>
            // </div>
            <Gallery images={IMAGES}/>,
            document.getElementById('example-0')
        );
    }

    handleVote(id, mood) {
        this.props.onVote(id, mood);
    }
}
